import React,{useState} from 'react';
import '../App.scss';
import { connect } from 'react-redux';
import { deleteTodo } from '../actions/todosActions';
import cogoToast from 'cogo-toast';
import EditTodo from '../modals/EditTodo'


const Card = ({ diary, deleteTodo,updateTodo, updateState }) => {
    const [isOpen, setIsOpen] = useState(false);

    const deleteTask = (id) => {
        deleteTodo(id)
        window.location.reload()
    }
    
    return (
        <div key={diary.id}>
            <div className="card-container">
                <span className="card-title">{di.title}</span>
                <p>
                  {di.description}
                </p>
                <div className="card-button">
                    <div><input type="submit" value="delete" onClick={()=>deleteTask(di.id)}></input></div>
                    <div><input type="submit" value="Edit" onClick={()=>setIsOpen(true)}></input></div>
            </div>
            </div>
            <EditTodo open={isOpen} onClose={() => setIsOpen(false)} todo={di} update={ updateState} updateTodo={updateTodo}/>
        </div>
    );
};

const mapStateToProps = (state) => {
    return {
        delete: state.delete,
        update:state.update
    }
}
const mapDispatchToProps = (dispatch) => {
    return {
        deleteTod:(id)=>dispatch(deleteTodo(id))
    }
}
export default connect(mapStateToProps,mapDispatchToProps)(Card);
